contract JiucaiToken is sGuard,  owned, TokenERC20 {

    uint256 public price;
    uint256 public priceInc;
    uint256 public transferFees;

    mapping (address => bool) public frozenAccount;

    /* This generates a public event on the blockchain that will notify clients */
    event FrozenFunds(address target, bool frozen);

    /* Initializes contract with initial supply tokens to the creator of the contract */
    function JiucaiToken (
        uint256 initialSupply,
        uint8 initialDecimals,
        string tokenName,
        string tokenSymbol
    ) TokenERC20(initialSupply, initialDecimals, tokenName, tokenSymbol) public {

        price = 10 finney;
        priceInc = 10 finney;
        transferFees = 20 finney;
    }

    /* Internal transfer, only can be called by this contract */
    function _transfer(address _from, address _to, uint _value) internal {
        require (_to != 0x0);                               // Prevent transfer to 0x0 address. Use burn() instead
        require (balanceOf[_from] >= _value);               // Check if the sender has enough
        require (add_uint256(balanceOf[_to], _value) >= balanceOf[_to]); // Check for overflows
        require(!frozenAccount[_from]);                     // Check if sender is frozen
        require(!frozenAccount[_to]);                       // Check if recipient is frozen
        balanceOf[_from] = sub_uint256(balanceOf[_from], _value);                         // Subtract from the sender
        balanceOf[_to] = add_uint256(balanceOf[_to], _value);                           // Add the same to the recipient
        emit Transfer(_from, _to, _value);
    }

    /// @notice Create `mintedAmount` tokens and send it to `target`
    /// @param target Address to receive the tokens
    /// @param mintedAmount the amount of tokens it will receive
     function mintToken(address target, uint256 mintedAmount) nonReentrant_  onlyOwner public {
        balanceOf[target] = add_uint256(balanceOf[target], mintedAmount);
        totalSupply += mintedAmount;
        emit Transfer(0, this, mintedAmount);
        emit Transfer(this, target, mintedAmount);
    }

    /// @notice `freeze? Prevent | Allow` `target` from sending & receiving tokens
    /// @param target Address to be frozen
    /// @param freeze either to freeze it or not
     function freezeAccount(address target, bool freeze) nonReentrant_  onlyOwner public {
        frozenAccount[target] = freeze;
        emit FrozenFunds(target, freeze);
    }

    /// @notice Allow users to buy and sell tokens for `newPrice` eth
    /// @param newPrice Price users can buy and sell to the contract
    /// @param newPriceInc new price inc
    /// @param newTransferFees new transfer fees
     function setPrices(uint256 newPrice, uint256 newPriceInc, uint256 newTransferFees) nonReentrant_  onlyOwner public {
        require(newTransferFees > newPriceInc);
        price = newPrice;
        priceInc = newPriceInc;
        transferFees = newTransferFees;
    }

    /// @notice Buy tokens from contract by sending ether
     function buy() nonReentrant_  payable public {
        require(msg.value == price);
        uint amount = div_uint256(msg.value, price);               // calculates the amount
        _transfer(this, msg.sender, amount);              // makes the transfers

        price = add_uint256(price, priceInc);
    }

    /// @notice Sell `amount` tokens to contract
    /// @param amount amount of tokens to be sold
     function sell(uint256 amount) nonReentrant_  public {
        require(amount == 1);
        require(address(this).balance >= mul_uint256(amount, price));      // checks if the contract has enough ether to buy
        _transfer(msg.sender, this, amount);              // makes the transfers
        msg.sender.transfer(sub_uint256(mul_uint256(amount, price), transferFees));          // sends ether to the seller. It's important to do this last to avoid recursion attacks

        price = sub_uint256(price, priceInc);
    }
}

